import "./card.scss";

const Card = ({ stats, description, image }) => {
  return (
    <div className="card">
      <div className="image">
        <img src={image} />
      </div>
      <div className="stats">
        <span className="num"> {stats}+ </span>
        <span className="text">{description}</span>
      </div>
    </div>
  );
};

export default Card;
